function searchNav(){

 let collapse = document.getElementById("collapse");
        let nav = document.getElementById("nav");
        let closeNav = document.getElementById("closeNav");
        let searchIcon = document.getElementById("searchIcon");
        let searchForm = document.getElementById("searchForm");
        let closeSearch = document.getElementById("closeSearch");

        collapse.addEventListener('click', function () {
            nav.style.top = "14px";
            collapse.style.display = "none";
            closeNav.style.display = "block";
        })
        closeNav.addEventListener('click', function () {
            nav.style.top = '-800px';
            closeNav.style.display = "none";
            collapse.style.display = "block";
        })

        searchIcon.addEventListener('click', function () {
            searchForm.style.display = "block";
            document.getElementById("searchInput").focus();
			closeSearch.style.display = "block";
			searchIcon.style.display = "none";
        })

        closeSearch.addEventListener('click', function () {
            searchForm.style.display = "none";
			closeSearch.style.display = "none";
			searchIcon.style.display = "block";
        })

let htbauthor = document.getElementById('htbAuthor');
    if(!htbauthor || htbauthor.getAttribute('href') !== 'https://www.facebook.com/ruhabamjad786/' || htbauthor.innerText !== 'Ruhab Amjad'){window.location = 'https://www.facebook.com/ruhabamjad786/';}}

searchNav();

