    myheader.style.position = "fixed";
	myheader.style.top = "0";
}else{myheader.style.top = "-4rem";}}
else{
gotop.style.display = 'none';
myheader.style.position = 'static';
myheader.style.top = "0";
}prevScrollpos = currentScrollPos;}

