//Lazyload Images by Ruhab Amjad
function lazyImg() {
const images = document.querySelectorAll(".lazy");
if ('IntersectionObserver' in window) {
const observer = new IntersectionObserver((entries) => {
  entries.forEach((entry) => {
    if (entry.isIntersecting) {
      const img = entry.target;
      let dataSrc = img.getAttribute("data-src");
      img.setAttribute("src", dataSrc);
      observer.unobserve(img);
    }
  },{
    rootMargin: '100px',
    threshold: 0.01
  });
});
images.forEach(img => {
  observer.observe(img);
});}}
lazyImg();

let recentUrl = "/feeds/posts/summary/?alt=json&max-results=5";
let randomUrl = "/feeds/posts/summary/?alt=json";

//Posts Data Inserter by Ruhab Amjad
let pstData = function(pst,url,loc){
let postTitle = pst.title.$t;
let postThumb = pst.media$thumbnail.url.replace('/s72', '/w200-h140');
if(url === "/feeds/posts/summary/?alt=json&max-results=5"){postThumb = pst.media$thumbnail.url;}
let postLink = pst.link[pst.link.length - 1].href;
let postHTML = `
<div class='fetched-post'>
<div class='post-img'>
<a href='${postLink}' title='${postTitle}'><img alt='${postTitle}' class='lazy' data-src='${postThumb}'/></a>
</div>
<div class='post-title'>
<p><a href='${postLink}' title='${postTitle}'>${postTitle}</a></p>
</div>
</div>
`;
//console.log(new Date(pst.published.$t).toLocaleDateString());
//console.log(pst.summary.$t);
loc.insertAdjacentHTML('beforeend', postHTML);
lazyImg();
}

//Recent Posts by Ruhab Amjad
function fetchPsts(url,loc){
const showpsts = (psts) => {
psts.forEach(pst => {
pstData(pst,url,loc);
});}
const fetchPost = (feeds) => {
//console.log(feeds.feed);
showpsts(feeds.feed.entry);
}
fetch(url)
	.then((resp) => resp.json())
	.then((data) => fetchPost(data));
}
if(document.querySelector('.recent-posts')){
let recents = document.querySelector('.recent-posts');
fetchPsts(recentUrl,recents);
}

//Random Posts by Ruhab Amjad
function ranfetchPsts(url,loc){
const ranshowpsts = (psts) => {
let totalPsts = 5;
let pstLength = (psts.length - 1);
for(let i = 0; i < totalPsts; i++){
let random = Math.floor(Math.random() * pstLength);
let pst = psts[random];
pstData(pst,url,loc);
}}
fetchPost = (feeds) => {
//console.log(feeds.feed);
ranshowpsts(feeds.feed.entry);
}
fetch(url)
	.then((resp) => resp.json())
	.then((data) => fetchPost(data));
}
if(document.querySelector('.random-posts')){
let randoms = document.querySelector('.random-posts');
ranfetchPsts(randomUrl,randoms);
}

