//TOC by Ruhab Amjad
const headings = document.querySelectorAll('main article .post-body h2,main article .post-body h3');
if(headings){
if(document.querySelector(".mytoc")){
document.querySelector(".mytoc span").addEventListener("click", function () {
    this.parentElement.classList.toggle("block");
});
}
for (let i = 0; i < headings.length; i++) {
            let allheadings = headings[i];
            if (!allheadings.hasAttribute('id')) {
                allheadings.setAttribute('id', 'h' + i);
                let myli = document.createElement('li');
                let mylink = document.createElement('a');
                mylink.setAttribute('href', '#' + allheadings.getAttribute('id'));
                mylink.appendChild(document.createTextNode(allheadings.textContent));
                myli.appendChild(mylink);
                if(document.getElementById('mytoc')){
				document.getElementById('mytoc').appendChild(myli);
				}
            }
 		}
	}

//Related Posts by Ruhab Amjad
let relTag = document.querySelector('.related-tag').getAttribute('data-label');
let relatedUrl = `/feeds/posts/summary/-/${relTag}?alt=json&max-results=3`;
if(document.querySelector('.related-content')){
let relateds = document.querySelector('.related-content');
fetchPsts(relatedUrl,relateds);
}

//Next Previous Titles by Ruhab Amjad
function prevNext(link,postP){
let Link = link;
fetch(Link)
.then(response => response.text())
.then(data => {
let parser = new DOMParser();
let htmlDoc = parser.parseFromString(data, "text/html");
let postTitle = htmlDoc.querySelector(".post-title").textContent;
postP.textContent = postTitle;
});}
if(document.querySelector("a.prev-post-link")){
let olderPostLink = document.querySelector("a.prev-post-link").getAttribute("href");
let prevPostP = document.querySelector(".prev-post-p");
prevNext(olderPostLink,prevPostP);}
if(document.querySelector("a.next-post-link")){
let newerPostLink = document.querySelector("a.next-post-link").getAttribute("href");
let nextPostP = document.querySelector(".next-post-p");
prevNext(newerPostLink,nextPostP);}

