//LoadMore Posts by Ruhab Amjad
let olderLink = document.querySelector('a.load-more').getAttribute("href");
document.querySelector('.load-more').addEventListener('click', function(e){
	document.querySelector('.loadMore').style.display = 'none';
	document.querySelector('.loading').style.display = 'inline-block';
    var xhr = new XMLHttpRequest();
    xhr.open('GET', olderLink, true);
    xhr.onreadystatechange = function() {
        if (xhr.readyState === 4 && xhr.status === 200) {
            let html = xhr.responseText;
            let parser = new DOMParser();
            let doc = parser.parseFromString(html, "text/html");
            let posts = doc.querySelector('.blog-posts');
			let pagr = doc.querySelector('.that-all');
			if(pagr){document.querySelector('.blog-pager').innerHTML = pagr.outerHTML;}
            document.querySelector('.blog-posts').insertAdjacentHTML('beforeend', posts.innerHTML);
			lazyImg();
			if(doc.querySelector('a.load-more')){
            olderLink = doc.querySelector('a.load-more').getAttribute("href");
			document.querySelector('.loadMore').style.display = 'inline-block';
			document.querySelector('.loading').style.display = 'none';
		  	}
        }
    };
    xhr.send();
e.preventDefault();
});

