//TOC by Ruhab Amjad
const headings = document.querySelectorAll('main article .post-body h2,main article .post-body h3');
if(headings){
if(document.querySelector(".mytoc")){
document.querySelector(".mytoc span").addEventListener("click", function () {
    this.parentElement.classList.toggle("block");
});}
for (let i = 0; i < headings.length; i++) {
            let allheadings = headings[i];
            if (!allheadings.hasAttribute('id')) {
                allheadings.setAttribute('id', 'h' + i);
                let myli = document.createElement('li');
                let mylink = document.createElement('a');
                mylink.setAttribute('href', '#' + allheadings.getAttribute('id'));
                mylink.appendChild(document.createTextNode(allheadings.textContent));
                myli.appendChild(mylink);
                if(document.getElementById('mytoc')){
				document.getElementById('mytoc').appendChild(myli);
				}}}}

